mapper.insert:
  
      select members_seq.NEXTVAL from dual
      
        
		INSERT INTO members(
			 mnum
			,name
            , ID
            , password
            , hp
            , gender
            , condition
            , useryn
            , gosuyn
            , adminyn
            , photo
        ) VALUES (
        	 #{mnum}
          	  ,#{name}
           	  , #{ID}
              , #{password}
              , #{hp}
               , #{gender}
               , #{gender}
               , #{useryn}
               , #{gosuyn}
               , #{adminyn}
               , #{photo}
          
        )

mapper.select:
SELECT count (*)	
	FROM members	
	WHERE ID= #{id} and password = #{password}

mapper.delete:
delete 
from members
where ID= #{id} and password = #{password}
